import { Container, MovieList, Movie } from "./style";
import {useState, useEffect} from 'react'

function Home(){

    const [movies, setMovies] = useState([])

    useEffect(() => {
        fetch('https://api.themoviedb.org/3/movie/popular?api_key=<<y>>&language=en-US&page=1')
    },[])

    return(
        <Container>
            <h1>Movies</h1>
            <MovieList>
                {movies.map(movie => {
                    return(
                        <Movie>
                            <a href=""><img src={movie.image_url} alt={movie.title}></img></a>
                            <span>{movie.title}</span>
                        </Movie>
                    )
                })}
            </MovieList>
        </Container>
    )

}



export default Home;
