import styled from 'styled-components'

export const Container = styled.div`
    h1{
        text-align: center;
        margin: 4rem 0;
    }
    ul{

    li{
        display: flex;
        flex-direction: column;
        align-items: center;
    }
    img{
        width: 180px;
        border-radius: 1rem;
        margin-bottom: 2rem;
    }
    span{
        font-weight: bold;
        font-size: 120%;
    }
    a{
        transition: all 0.3s;
    }

    a:hover{
        transform: scale(1.1);
    }
`
export const MovieList = styled.ul`

`
